{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": [
    ".env",
    ".env.local"
  ],
  "globalPassThroughEnv": [
    "NODE_ENV",
    "CI",
    "RUST_LOG",
    "OPENAI_API_KEY",
    "ANTHROPIC_API_KEY"
  ],
  "ui": "tui",
  "tasks": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**", "build/**", ".next/**", "target/**"]
    },
    "build:rust": {
      "dependsOn": [],
      "outputs": ["target/**"],
      "cache": true
    },
    "build:wasm": {
      "dependsOn": ["build:rust"],
      "outputs": ["pkg/**"],
      "cache": true
    },
    "dev": {
      "cache": false,
      "persistent": true
    },
    "lint": {
      "dependsOn": ["^lint"],
      "outputs": []
    },
    "lint:rust": {
      "dependsOn": [],
      "outputs": []
    },
    "test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"],
      "cache": false
    },
    "test:rust": {
      "dependsOn": ["build:rust"],
      "outputs": [],
      "cache": false
    },
    "typecheck": {
      "dependsOn": ["^typecheck"],
      "outputs": []
    },
    "clean": {
      "cache": false
    }
  }
}

[package]
name = "snps-core"
version.workspace = true
edition.workspace = true
license.workspace = true
description = "PMSynapse core library - knowledge graph, LLM integration, IDLC workflow"

[lib]
name = "snps_core"
path = "src/lib.rs"

[features]
default = []

[dependencies]
# Workspace dependencies
tokio = { workspace = true }
serde = { workspace = true }
serde_json = { workspace = true }
anyhow = { workspace = true }
thiserror = { workspace = true }
async-trait = { workspace = true }
tracing = { workspace = true }

# Storage - SQLite for MVP
rusqlite = { workspace = true }

# LLM integration
reqwest = { workspace = true }

# UUID generation
uuid = { version = "1", features = ["v4", "serde"] }

# Date/time
chrono = { version = "0.4", features = ["serde"] }

[dev-dependencies]
insta = { workspace = true }
tokio-test = "0.4"

