1:/**
2: * @pmsynapse/rpc
3: *
4: * Shared RPC types and utilities for communication between:
5: * - VS Code extension  Webview
6: * - Tauri frontend  Backend
7: * - CLI  Core library
8: */
9:
10:// ============================================================================
11:// Message Types
12:// ============================================================================
13:
14:/**
15: * Base message structure for all RPC communication
16: */
17:export interface RpcMessage<T = unknown> {
18:  id: string;
19:  type: string;
20:  payload?: T;
21:  timestamp: number;
22:}
23:
24:/**
25: * Request message (client  server)
26: */
27:export interface RpcRequest<T = unknown> extends RpcMessage<T> {
28:  method: string;
29:}
30:
31:/**
32: * Response message (server  client)
33: */
34:export interface RpcResponse<T = unknown> extends RpcMessage<T> {
35:  requestId: string;
36:  success: boolean;
37:  error?: RpcError;
38:}
39:
40:/**
41: * Error details
42: */
43:export interface RpcError {
44:  code: string;
45:  message: string;
46:  details?: unknown;
47:}
48:
49:// ============================================================================
50:// IDLC Types (shared between all frontends)
51:// ============================================================================
52:
53:/**
54: * Node types in the knowledge graph
55: */
56:export type NodeType =
57:  | "idea"
58:  | "feature"
59:  | "task"
60:  | "decision"
61:  | "question"
62:  | "assumption"
63:  | "code"
64:  | "test"
65:  | "document"
66:  | "research"
67:  | "plan"
68:  | "completion";
69:
70:/**
71: * Edge types in the knowledge graph
72: */
73:export type EdgeType =
74:  | "inspires"
75:  | "requires"
76:  | "produces"
77:  | "impacts"
78:  | "blocks"
79:  | "validates"
80:  | "implements"
81:  | "verifies"
82:  | "describes"
83:  | "informs"
84:  | "enables"
85:  | "completes";
86:
87:/**
88: * A node in the knowledge graph
89: */
90:export interface KnowledgeNode {
91:  id: string;
92:  nodeType: NodeType;
93:  title: string;
94:  content: string;
95:  confidence: number;
96:  createdAt: string;
97:  updatedAt: string;
98:  metadata?: Record<string, unknown>;
99:}
100:
101:/**
102: * An edge in the knowledge graph
103: */
104:export interface KnowledgeEdge {
105:  id: string;
106:  fromNode: string;
107:  toNode: string;
108:  edgeType: EdgeType;
109:  confidence: number;
110:  createdAt: string;
111:  metadata?: Record<string, unknown>;
112:}
113:
114:// ============================================================================
115:// API Methods
116:// ============================================================================
117:
118:/**
119: * Available RPC methods
120: */
121:export const RpcMethods = {
122:  // Node operations
123:  CREATE_NODE: "node.create",
124:  GET_NODE: "node.get",
125:  UPDATE_NODE: "node.update",
126:  DELETE_NODE: "node.delete",
127:  LIST_NODES: "node.list",
128:  QUERY_NODES: "node.query",
129:
130:  // Edge operations
131:  CREATE_EDGE: "edge.create",
132:  GET_EDGE: "edge.get",
133:  DELETE_EDGE: "edge.delete",
134:  LIST_EDGES: "edge.list",
135:
136:  // Graph operations
137:  GET_RELATED: "graph.related",
138:  GET_SUBGRAPH: "graph.subgraph",
139:  SEARCH: "graph.search",
140:
141:  // LLM operations
142:  COMPLETE: "llm.complete",
143:  EMBED: "llm.embed",
144:  SUGGEST: "llm.suggest",
145:
146:  // System operations
147:  HEALTH: "system.health",
148:  CONFIG: "system.config",
149:} as const;
150:
151:// ============================================================================
152:// Request/Response Payloads
153:// ============================================================================
154:
155:export interface CreateNodeRequest {
156:  nodeType: NodeType;
157:  title: string;
158:  content: string;
159:  confidence?: number;
160:  metadata?: Record<string, unknown>;
161:}
162:
163:export interface CreateNodeResponse {
164:  node: KnowledgeNode;
165:}
166:
167:export interface QueryNodesRequest {
168:  nodeType?: NodeType;
169:  query?: string;
170:  limit?: number;
171:  offset?: number;
172:}
173:
174:export interface QueryNodesResponse {
175:  nodes: KnowledgeNode[];
176:  total: number;
177:}
178:
179:export interface CreateEdgeRequest {
180:  fromNode: string;
181:  toNode: string;
182:  edgeType: EdgeType;
183:  confidence?: number;
184:  metadata?: Record<string, unknown>;
185:}
186:
187:export interface CreateEdgeResponse {
188:  edge: KnowledgeEdge;
189:}
190:
191:export interface GetRelatedRequest {
192:  nodeId: string;
193:  depth?: number;
194:  edgeTypes?: EdgeType[];
195:}
196:
197:export interface GetRelatedResponse {
198:  nodes: KnowledgeNode[];
199:  edges: KnowledgeEdge[];
200:}
201:
202:export interface LlmCompleteRequest {
203:  prompt: string;
204:  model?: string;
205:  maxTokens?: number;
206:  temperature?: number;
207:  systemPrompt?: string;
208:}
209:
210:export interface LlmCompleteResponse {
211:  content: string;
212:  model: string;
213:  usage: {
214:    promptTokens: number;
215:    completionTokens: number;
216:    totalTokens: number;
217:  };
218:}
219:
220:// ============================================================================
221:// Utilities
222:// ============================================================================
223:
224:/**
225: * Generate a unique message ID
226: */
227:export function generateMessageId(): string {
228:  return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
229:}
230:
231:/**
232: * Create an RPC request
233: */
234:export function createRequest<T>(method: string, payload?: T): RpcRequest<T> {
235:  return {
236:    id: generateMessageId(),
237:    type: "request",
238:    method,
239:    payload,
240:    timestamp: Date.now(),
241:  };
242:}
243:
244:/**
245: * Create an RPC response
246: */
247:export function createResponse<T>(
248:  requestId: string,
249:  payload?: T,
250:  error?: RpcError
251:): RpcResponse<T> {
252:  return {
253:    id: generateMessageId(),
254:    type: "response",
255:    requestId,
256:    success: !error,
257:    payload,
258:    error,
259:    timestamp: Date.now(),
260:  };
261:}
262:
263:/**
264: * Create an error response
265: */
266:export function createErrorResponse(
267:  requestId: string,
268:  code: string,
269:  message: string,
270:  details?: unknown
271:): RpcResponse<undefined> {
272:  return createResponse(requestId, undefined, { code, message, details });
273:}

