[package]
name = "pmsynapse-desktop"
version.workspace = true
edition.workspace = true
license.workspace = true
description = "PMSynapse Desktop Application"

[lib]
name = "pmsynapse_desktop_lib"
crate-type = ["lib", "cdylib", "staticlib"]

[build-dependencies]
tauri-build = { workspace = true, features = [] }

[dependencies]
# Core library
snps-core = { path = "../../../engine/snps-core" }

# Tauri
tauri = { workspace = true, features = [] }
tauri-plugin-fs = { workspace = true }
tauri-plugin-shell = { workspace = true }
tauri-plugin-clipboard-manager = { workspace = true }
tauri-plugin-notification = { workspace = true }
tauri-plugin-log = { workspace = true }
tauri-plugin-store = { workspace = true }

# Workspace dependencies
serde = { workspace = true }
serde_json = { workspace = true }
tokio = { workspace = true }
tracing = { workspace = true }

[features]
default = ["custom-protocol"]
custom-protocol = ["tauri/custom-protocol"]

{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": [
    ".env",
    ".env.local",
    "Cargo.toml",
    "Cargo.lock"
  ],
  "globalPassThroughEnv": [
    "NODE_ENV",
    "CI",
    "GITHUB_ACTIONS",
    "RUST_LOG",
    "RUST_BACKTRACE",
    "OPENAI_API_KEY",
    "ANTHROPIC_API_KEY",
    "CARGO_TERM_COLOR"
  ],
  "ui": "tui",
  "concurrency": "10",
  "tasks": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**", "build/**", ".next/**", "out/**"],
      "inputs": ["src/**", "package.json", "tsconfig.json"]
    },
    "build:engine": {
      "dependsOn": [],
      "outputs": ["../../target/release/**"],
      "inputs": ["../../engine/**/*.rs", "../../Cargo.toml", "../../Cargo.lock"],
      "cache": true,
      "env": ["CARGO_TERM_COLOR"]
    },
    "build:debug": {
      "dependsOn": [],
      "outputs": ["../../target/debug/**"],
      "inputs": ["../../engine/**/*.rs", "../../Cargo.toml", "../../Cargo.lock"],
      "cache": true
    },
    "build:tauri": {
      "dependsOn": ["build:engine", "build"],
      "outputs": ["src-tauri/target/**"],
      "cache": true
    },
    "dev": {
      "cache": false,
      "persistent": true
    },
    "lint": {
      "dependsOn": ["^lint"],
      "outputs": [],
      "inputs": ["src/**", "*.json", "*.js", "*.ts"]
    },
    "lint:engine": {
      "dependsOn": [],
      "outputs": [],
      "inputs": ["../../engine/**/*.rs"],
      "cache": true
    },
    "test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"],
      "cache": false,
      "env": ["CI"]
    },
    "test:engine": {
      "dependsOn": ["build:debug"],
      "outputs": [],
      "cache": false,
      "env": ["CI", "RUST_BACKTRACE"]
    },
    "typecheck": {
      "dependsOn": ["^build"],
      "outputs": [],
      "inputs": ["src/**", "tsconfig.json"]
    },
    "release": {
      "dependsOn": ["^release", "build", "test"],
      "outputs": [],
      "cache": false,
      "env": ["CI", "GITHUB_TOKEN", "NPM_TOKEN"]
    },
    "release:engine": {
      "dependsOn": ["lint:engine", "test:engine"],
      "outputs": ["../../target/release/**"],
      "cache": false,
      "env": ["CI", "CARGO_REGISTRY_TOKEN"]
    },
    "release:vscode": {
      "dependsOn": ["^build", "build", "test"],
      "outputs": ["*.vsix"],
      "cache": false,
      "env": ["VSCE_PAT"]
    },
    "release:desktop": {
      "dependsOn": ["build:tauri"],
      "outputs": ["src-tauri/target/release/bundle/**"],
      "cache": false,
      "env": ["TAURI_SIGNING_PRIVATE_KEY"]
    },
    "clean": {
      "cache": false
    },
    "package": {
      "dependsOn": ["build"],
      "outputs": ["*.vsix", "*.tgz"],
      "cache": true
    }
  }
}
