     1→import * as vscode from "vscode";
     2→import { getUri } from "../utils/getUri";
     3→import { getNonce } from "../utils/getNonce";
     4→
     5→/**
     6→ * WebviewPanelHost manages the webview panel that hosts the PMSynapse UI.
     7→ *
     8→ * This follows the BAML pattern for dual deployment:
     9→ * - In development: loads from Vite dev server (hot reload)
    10→ * - In production: loads bundled assets with CSP
    11→ */
    12→export class WebviewPanelHost {
    13→  public static readonly viewType = "pmsynapse.playground";
    14→
    15→  private readonly _panel: vscode.WebviewPanel;
    16→  private readonly _extensionUri: vscode.Uri;
    17→  private _disposables: vscode.Disposable[] = [];
    18→  private _onDidDispose: (() => void) | undefined;
    19→
    20→  constructor(extensionUri: vscode.Uri, context: vscode.ExtensionContext) {
    21→    this._extensionUri = extensionUri;
    22→
    23→    // Create the webview panel
    24→    this._panel = vscode.window.createWebviewPanel(
    25→      WebviewPanelHost.viewType,
    26→      "PMSynapse",
    27→      vscode.ViewColumn.One,
    28→      {
    29→        enableScripts: true,
    30→        retainContextWhenHidden: true,
    31→        localResourceRoots: [
    32→          vscode.Uri.joinPath(extensionUri, "dist"),
    33→          vscode.Uri.joinPath(extensionUri, "webview-ui", "dist"),
    34→        ],
    35→      }
    36→    );
    37→
    38→    // Set the HTML content
    39→    this._panel.webview.html = this._getWebviewContent();
    40→
    41→    // Handle messages from the webview
    42→    this._panel.webview.onDidReceiveMessage(
    43→      (message) => this._handleMessage(message),
    44→      null,
    45→      this._disposables
    46→    );
    47→
    48→    // Handle panel disposal
    49→    this._panel.onDidDispose(
    50→      () => this._dispose(),
    51→      null,
    52→      this._disposables
    53→    );
    54→  }
    55→
    56→  /**
    57→   * Register a callback for when the panel is disposed
    58→   */
    59→  public onDidDispose(callback: () => void): void {
    60→    this._onDidDispose = callback;
    61→  }
    62→
    63→  /**
    64→   * Reveal the panel
    65→   */
    66→  public reveal(): void {
    67→    this._panel.reveal();
    68→  }
    69→
    70→  /**
    71→   * Post a message to the webview
    72→   */
    73→  public postMessage(message: unknown): void {
    74→    this._panel.webview.postMessage(message);
    75→  }
    76→
    77→  /**
    78→   * Dispose of the panel
    79→   */
    80→  public dispose(): void {
    81→    this._panel.dispose();
    82→  }
    83→
    84→  private _dispose(): void {
    85→    // Clean up resources
    86→    while (this._disposables.length) {
    87→      const disposable = this._disposables.pop();
    88→      if (disposable) {
    89→        disposable.dispose();
    90→      }
    91→    }
    92→
    93→    // Call the dispose callback
    94→    if (this._onDidDispose) {
    95→      this._onDidDispose();
    96→    }
    97→  }
    98→
    99→  /**
   100→   * Handle messages from the webview
   101→   */
   102→  private _handleMessage(message: { type: string; payload?: unknown }): void {
   103→    switch (message.type) {
   104→      case "ready":
   105→        console.log("PMSynapse webview is ready");
   106→        break;
   107→
   108→      case "createIdlcItem":
   109→        vscode.commands.executeCommand("pmsynapse.createIdlcItem");
   110→        break;
   111→
   112→      case "showKnowledgeGraph":
   113→        // Handle knowledge graph display
   114→        break;
   115→
   116→      case "executeCommand":
   117→        if (typeof message.payload === "string") {
   118→          vscode.commands.executeCommand(message.payload);
   119→        }
   120→        break;
   121→
   122→      case "showMessage":
   123→        if (typeof message.payload === "string") {
   124→          vscode.window.showInformationMessage(message.payload);
   125→        }
   126→        break;
   127→
   128→      case "error":
   129→        if (typeof message.payload === "string") {
   130→          vscode.window.showErrorMessage(message.payload);
   131→        }
   132→        break;
   133→
   134→      default:
   135→        console.log("Unknown message type:", message.type);
   136→    }
   137→  }
   138→
   139→  /**
   140→   * Get the HTML content for the webview
   141→   */
   142→  private _getWebviewContent(): string {
   143→    const webview = this._panel.webview;
   144→    const nonce = getNonce();
   145→
   146→    // Check if we're in development mode
   147→    const isDev = process.env.VSCODE_DEBUG_MODE === "true";
   148→
   149→    if (isDev) {
   150→      // Development mode: load from Vite dev server
   151→      return this._getDevContent(nonce);
   152→    }
   153→
   154→    // Production mode: load bundled assets
   155→    return this._getProdContent(webview, nonce);
   156→  }
   157→
   158→  /**
   159→   * Get development mode HTML (loads from Vite dev server)
   160→   */
   161→  private _getDevContent(nonce: string): string {
   162→    const devServerUrl = "http://localhost:3030";
   163→
   164→    return `<!DOCTYPE html>
   165→<html lang="en">
   166→<head>
   167→  <meta charset="UTF-8">
   168→  <meta name="viewport" content="width=device-width, initial-scale=1.0">
   169→  <title>PMSynapse</title>
   170→</head>
   171→<body>
   172→  <div id="root"></div>
   173→  <script type="module" nonce="${nonce}">
   174→    import RefreshRuntime from "${devServerUrl}/@react-refresh";
   175→    RefreshRuntime.injectIntoGlobalHook(window);
   176→    window.$RefreshReg$ = () => {};
   177→    window.$RefreshSig$ = () => (type) => type;
   178→    window.__vite_plugin_react_preamble_installed__ = true;
   179→  </script>
   180→  <script type="module" src="${devServerUrl}/@vite/client"></script>
   181→  <script type="module" src="${devServerUrl}/src/main.tsx"></script>
   182→</body>
   183→</html>`;
   184→  }
   185→
   186→  /**
   187→   * Get production mode HTML (loads bundled assets)
   188→   */
   189→  private _getProdContent(webview: vscode.Webview, nonce: string): string {
   190→    // Get URIs for bundled assets
   191→    const stylesUri = getUri(webview, this._extensionUri, [
   192→      "webview-ui",
   193→      "dist",
   194→      "assets",
   195→      "index.css",
   196→    ]);
   197→    const scriptUri = getUri(webview, this._extensionUri, [
   198→      "webview-ui",
   199→      "dist",
   200→      "assets",
   201→      "index.js",
   202→    ]);
   203→
   204→    // Content Security Policy
   205→    const csp = [
   206→      `default-src 'none'`,
   207→      `style-src ${webview.cspSource} 'unsafe-inline'`,
   208→      `script-src 'nonce-${nonce}'`,
   209→      `font-src ${webview.cspSource}`,
   210→      `img-src ${webview.cspSource} https: data:`,
   211→      `connect-src https:`,
   212→    ].join("; ");
   213→
   214→    return `<!DOCTYPE html>
   215→<html lang="en">
   216→<head>
   217→  <meta charset="UTF-8">
   218→  <meta name="viewport" content="width=device-width, initial-scale=1.0">
   219→  <meta http-equiv="Content-Security-Policy" content="${csp}">
   220→  <link rel="stylesheet" type="text/css" href="${stylesUri}">
   221→  <title>PMSynapse</title>
   222→</head>
