{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": [
    ".env",
    ".env.local",
    "Cargo.toml",
    "Cargo.lock"
  ],
  "globalPassThroughEnv": [
    "NODE_ENV",
    "CI",
    "GITHUB_ACTIONS",
    "RUST_LOG",
    "RUST_BACKTRACE",
    "OPENAI_API_KEY",
    "ANTHROPIC_API_KEY",
    "CARGO_TERM_COLOR"
  ],
  "ui": "tui",
  "concurrency": "10",
  "tasks": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**", "build/**", ".next/**", "out/**"],
      "inputs": ["src/**", "package.json", "tsconfig.json"]
    },
    "build:engine": {
      "dependsOn": [],
      "outputs": ["../../target/release/**"],
      "inputs": ["../../engine/**/*.rs", "../../Cargo.toml", "../../Cargo.lock"],
      "cache": true,
      "env": ["CARGO_TERM_COLOR"]
    },
    "build:debug": {
      "dependsOn": [],
      "outputs": ["../../target/debug/**"],
      "inputs": ["../../engine/**/*.rs", "../../Cargo.toml", "../../Cargo.lock"],
      "cache": true
    },
    "build:tauri": {
      "dependsOn": ["build:engine", "build"],
      "outputs": ["src-tauri/target/**"],
      "cache": true
    },
    "dev": {
      "cache": false,
      "persistent": true
    },
    "lint": {
      "dependsOn": ["^lint"],
      "outputs": [],
      "inputs": ["src/**", "*.json", "*.js", "*.ts"]
    },
    "lint:engine": {
      "dependsOn": [],
      "outputs": [],
      "inputs": ["../../engine/**/*.rs"],
      "cache": true
    },
    "test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"],
      "cache": false,
      "env": ["CI"]
    },
    "test:engine": {
      "dependsOn": ["build:debug"],
      "outputs": [],
      "cache": false,
      "env": ["CI", "RUST_BACKTRACE"]
    },
    "typecheck": {
      "dependsOn": ["^build"],
      "outputs": [],
      "inputs": ["src/**", "tsconfig.json"]
    },
    "release": {
      "dependsOn": ["^release", "build", "test"],
      "outputs": [],
      "cache": false,
      "env": ["CI", "GITHUB_TOKEN", "NPM_TOKEN"]
    },
    "release:engine": {
      "dependsOn": ["lint:engine", "test:engine"],
      "outputs": ["../../target/release/**"],
      "cache": false,
      "env": ["CI", "CARGO_REGISTRY_TOKEN"]
    },
    "release:vscode": {
      "dependsOn": ["^build", "build", "test"],
      "outputs": ["*.vsix"],
      "cache": false,
      "env": ["VSCE_PAT"]
    },
    "release:desktop": {
      "dependsOn": ["build:tauri"],
      "outputs": ["src-tauri/target/release/bundle/**"],
      "cache": false,
      "env": ["TAURI_SIGNING_PRIVATE_KEY"]
    },
    "clean": {
      "cache": false
    },
    "package": {
      "dependsOn": ["build"],
      "outputs": ["*.vsix", "*.tgz"],
      "cache": true
    }
  }
}

{
  "name": "pmsynapse",
  "displayName": "PMSynapse",
  "description": "AI-enabled project management with knowledge graphs and IDLC workflow",
  "version": "0.1.0",
  "publisher": "HelixoidLLC",
  "license": "Apache-2.0",
  "repository": {
    "type": "git",
    "url": "https://github.com/HelixoidLLC/pmsynapse"
  },
  "engines": {
    "vscode": "^1.85.0"
  },
  "categories": [
    "Other",
    "Visualization"
  ],
  "keywords": [
    "project-management",
    "knowledge-graph",
    "ai",
    "idlc",
    "workflow"
  ],
  "activationEvents": [
    "onStartupFinished"
  ],
  "main": "./dist/extension.js",
  "contributes": {
    "commands": [
      {
        "command": "pmsynapse.openPlayground",
        "title": "PMSynapse: Open Playground",
        "icon": "$(graph)"
      },
      {
        "command": "pmsynapse.createIdlcItem",
        "title": "PMSynapse: Create IDLC Item",
        "icon": "$(add)"
      },
      {
        "command": "pmsynapse.showKnowledgeGraph",
        "title": "PMSynapse: Show Knowledge Graph",
        "icon": "$(type-hierarchy)"
      }
    ],
    "viewsContainers": {
      "activitybar": [
        {
          "id": "pmsynapse",
          "title": "PMSynapse",
          "icon": "resources/icon.svg"
        }
      ]
    },
    "views": {
      "pmsynapse": [
        {
          "id": "pmsynapse.idlcItems",
          "name": "IDLC Items"
        },
        {
          "id": "pmsynapse.knowledgeGraph",
          "name": "Knowledge Graph"
        }
      ]
    },
    "configuration": {
      "title": "PMSynapse",
      "properties": {
        "pmsynapse.llmProvider": {
          "type": "string",
          "default": "openai",
          "enum": ["openai", "anthropic", "ollama"],
          "description": "Default LLM provider for AI features"
        },
        "pmsynapse.graphDatabasePath": {
          "type": "string",
          "default": ".synapse/graph.db",
          "description": "Path to the knowledge graph database"
        }
      }
    }
  },
  "scripts": {
    "dev": "tsup --watch",
    "build": "tsup",
    "typecheck": "tsc --noEmit",
    "lint": "eslint src --ext ts",
    "test": "echo 'No tests configured yet'",
    "clean": "rm -rf dist *.vsix",
    "package": "pnpm build && vsce package --no-dependencies",
    "release": "pnpm package",
    "publish": "vsce publish --no-dependencies"
  },
  "dependencies": {
    "@pmsynapse/rpc": "workspace:*"
  },
  "devDependencies": {
    "@types/vscode": "^1.85.0",
    "@vscode/vsce": "^3.0.0",
    "tsup": "^8.0.0",
    "typescript": "^5.7.0"
  }
}

{
  "name": "@pmsynapse/rpc",
  "version": "0.1.0",
  "description": "Shared RPC types and utilities for PMSynapse",
  "main": "dist/index.js",
  "module": "dist/index.mjs",
  "types": "dist/index.d.ts",
  "exports": {
    ".": {
      "types": "./dist/index.d.ts",
      "import": "./dist/index.mjs",
      "require": "./dist/index.js"
    }
  },
  "scripts": {
    "dev": "tsup --watch",
    "build": "tsup",
    "typecheck": "tsc --noEmit",
    "lint": "echo 'No linting configured'",
    "test": "echo 'No tests configured'",
    "clean": "rm -rf dist",
    "release": "pnpm build"
  },
  "devDependencies": {
    "tsup": "^8.0.0",
    "typescript": "^5.7.0"
  }
}

{
  "compilerOptions": {
    "target": "ES2020",
    "useDefineForClassFields": true,
    "lib": ["ES2020", "DOM", "DOM.Iterable"],
    "module": "ESNext",
    "skipLibCheck": true,

    /* Bundler mode */
    "moduleResolution": "bundler",
    "allowImportingTsExtensions": true,
    "resolveJsonModule": true,
    "isolatedModules": true,
    "noEmit": true,
    "jsx": "react-jsx",

    /* Linting */
    "strict": true,
    "noUnusedLocals": true,
    "noUnusedParameters": true,
    "noFallthroughCasesInSwitch": true,

    /* Path aliases */
    "baseUrl": ".",
    "paths": {
      "@/*": ["./src/*"]
    }
  },
  "include": ["src"],
  "references": [{ "path": "./tsconfig.node.json" }]
}

