1:/**
2: * @pmsynapse/rpc
3: *
4: * Shared RPC types and utilities for communication between:
5: * - VS Code extension ‚Üî Webview
6: * - Tauri frontend ‚Üî Backend
7: * - CLI ‚Üî Core library
8: */
9:
10:// ============================================================================
11:// Message Types
12:// ============================================================================
13:
14:/**
15: * Base message structure for all RPC communication
16: */
17:export interface RpcMessage<T = unknown> {
18:  id: string;
19:  type: string;
20:  payload?: T;
21:  timestamp: number;
22:}
23:
24:/**
25: * Request message (client ‚Üí server)
26: */
27:export interface RpcRequest<T = unknown> extends RpcMessage<T> {
28:  method: string;
29:}
30:
31:/**
32: * Response message (server ‚Üí client)
33: */
34:export interface RpcResponse<T = unknown> extends RpcMessage<T> {
35:  requestId: string;
36:  success: boolean;
37:  error?: RpcError;
38:}
39:
40:/**
41: * Error details
42: */
43:export interface RpcError {
44:  code: string;
45:  message: string;
46:  details?: unknown;
47:}
48:
49:// ============================================================================
50:// IDLC Types (shared between all frontends)
51:// ============================================================================
52:
53:/**
54: * Node types in the knowledge graph
55: */
56:export type NodeType =
57:  | "idea"
58:  | "feature"
59:  | "task"
60:  | "decision"
61:  | "question"
62:  | "assumption"
63:  | "code"
64:  | "test"
65:  | "document"
66:  | "research"
67:  | "plan"
68:  | "completion";
69:
70:/**
71: * Edge types in the knowledge graph
72: */
73:export type EdgeType =
74:  | "inspires"
75:  | "requires"
76:  | "produces"
77:  | "impacts"
78:  | "blocks"
79:  | "validates"
80:  | "implements"
81:  | "verifies"
82:  | "describes"
83:  | "informs"
84:  | "enables"
85:  | "completes";
86:
87:/**
88: * A node in the knowledge graph
89: */
90:export interface KnowledgeNode {
91:  id: string;
92:  nodeType: NodeType;
93:  title: string;
94:  content: string;
95:  confidence: number;
96:  createdAt: string;
97:  updatedAt: string;
98:  metadata?: Record<string, unknown>;
99:}
100:
101:/**
102: * An edge in the knowledge graph
103: */
104:export interface KnowledgeEdge {
105:  id: string;
106:  fromNode: string;
107:  toNode: string;
108:  edgeType: EdgeType;
109:  confidence: number;
110:  createdAt: string;
111:  metadata?: Record<string, unknown>;
112:}
113:
114:// ============================================================================
115:// API Methods
116:// ============================================================================
117:
118:/**
119: * Available RPC methods
120: */
121:export const RpcMethods = {
